chrome.runtime.onInstalled.addListener(()=>{chrome.contextMenus.create({id:"reportHateSpeech",title:"Report hate speech",contexts:["selection"]})});chrome.contextMenus.onClicked.addListener((e,t)=>{e.menuItemId==="reportHateSpeech"&&e.selectionText&&t.id!==void 0&&chrome.scripting.executeScript({target:{tabId:t.id},func:n=>{fetch("https://your-endpoint.example/report",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({content:n,url:window.location.href})}).catch(o=>console.error("Report failed",o))},args:[e.selectionText]})});
